            visibilityDisplay = (msg.payload.visibility / 1000).toFixed(1) + ' km';
        } else {
            visibilityDisplay = msg.payload.visibility + ' m';
        }
    }

    // Format cloud coverage
    let cloudsDisplay = 'Unknown';
