    msg.rainfall = msg.payload.rain_1h || 0;
    msg.snowfall = msg.payload.snow_1h || 0;
    msg.visibility = msg.payload.visibility;
    msg.clouds = msg.payload.clouds_percent;

    // Create status indicators for weather conditions
    msg.isRaining = (msg.payload.rain_1h && msg.payload.rain_1h > 0);
    msg.isSnowing = (msg.payload.snow_1h && msg.payload.snow_1h > 0);
    msg.hasLowVisibility = (msg.payload.visibility && msg.payload.visibility < 1);

    // Determine weather status for color-coded displays
