// Create an array to hold the formatted data for the table
const tableData = [];

// Function to create a more readable date format: Month Day, Day HH:MM
function formatDateTime(dateTimeStr) {
    const date = new Date(dateTimeStr);
    const days = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
    const months = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];

    const day = days[date.getDay()];
    const month = months[date.getMonth()];
    const dayOfMonth = date.getDate();
    const hours = date.getHours().toString().padStart(2, '0');
    const minutes = date.getMinutes().toString().padStart(2, '0');

    return `${month} ${dayOfMonth}, ${day} ${hours}:${minutes}`;
}

// Function to format visibility in a more readable way (convert to km if ‚â• 1000m)
function formatVisibility(visibilityInMeters) {
    if (!visibilityInMeters && visibilityInMeters !== 0) return "N/A";

    if (visibilityInMeters >= 1000) {
        // Convert to kilometers with one decimal place for values ‚â• 1000m
        return `${(visibilityInMeters / 1000).toFixed(1)} km`;
    } else {
        // Keep in meters for values < 1000m
        return `${visibilityInMeters} m`;
    }
}

// Process each forecast item
forecastItems.forEach(item => {
    // Format temperature with unit
    const tempC = item.temperature.toFixed(1);

    // Format humidity with unit
    const humidity = `${item.humidity}%`;

    // Format wind with unit
    const wind = `${item.wind_speed.toFixed(1)} m/s`;

    // Format rain
    let rain = "0 mm";
    if (item.rain_3h && item.rain_3h > 0) {
        rain = `${item.rain_3h.toFixed(2)} mm`;
    }

    // Format snow (added)
    let snow = "0 mm";
    if (item.snow_3h && item.snow_3h > 0) {
        snow = `${item.snow_3h.toFixed(2)} mm`;
    }

    // Format visibility (added)
    const visibility = formatVisibility(item.visibility);

    // Format pressure (added) - convert hPa to kPa for readability if needed
    const pressure = `${item.pressure} hPa`;

    // Push the formatted item to the table data array
    tableData.push({
