// Function to process and format weather alarms
function processWeatherAlarms(msg) {
    if (!msg.payload || !msg.payload.alarms || !Array.isArray(msg.payload.alarms)) {
        node.warn("No valid alarm data found");
        return {};
    }

    // Group alarms by severity
    const highSeverity = msg.payload.alarms.filter(alarm => alarm.severity === "high");
    const mediumSeverity = msg.payload.alarms.filter(alarm => alarm.severity === "medium");
    const lowSeverity = msg.payload.alarms.filter(alarm => alarm.severity === "low");

    // Group alarms by type (current vs forecast)
    const currentAlarms = msg.payload.alarms.filter(alarm => !alarm.type.startsWith("FORECAST_"));
    const forecastAlarms = msg.payload.alarms.filter(alarm => alarm.type.startsWith("FORECAST_"));

    // Format alarms for dashboard display
    const formattedAlarms = msg.payload.alarms.map((alarm, index) => {
        return {
            id: index + 1,
            type: alarm.type.replace("FORECAST_", "").replace(/_/g, " "),
            severity: alarm.severity,
            message: alarm.message,
            health_impact: alarm.health_impact,
            isForecast: alarm.type.startsWith("FORECAST_"),
            timestamp: msg.payload.timestamp
        };
    });

    // Sort alarms by severity (high first) and then by forecast (current before forecast)
    formattedAlarms.sort((a, b) => {
        // Sort by severity first
        if (a.severity === "high" && b.severity !== "high") return -1;
        if (a.severity !== "high" && b.severity === "high") return 1;
        if (a.severity === "medium" && b.severity === "low") return -1;
        if (a.severity === "low" && b.severity === "medium") return 1;

        // Then sort by forecast vs current
        if (!a.isForecast && b.isForecast) return -1;
        if (a.isForecast && !b.isForecast) return 1;

        return 0;
    });

    // Create summary info
    const alarmSummary = {
        location: msg.payload.location,
        timestamp: msg.payload.timestamp,
        total_alarms: msg.payload.alarm_count,
        high_severity_count: highSeverity.length,
        medium_severity_count: mediumSeverity.length,
        low_severity_count: lowSeverity.length,
        current_alarms_count: currentAlarms.length,
        forecast_alarms_count: forecastAlarms.length
    };

    // Return both the formatted alarms and the summary
    return { 
        payload: formattedAlarms,
        summary: alarmSummary
    };
}

// Process the incoming message
const result = processWeatherAlarms(msg);
if (result) {
    // Set output message
    msg.payload = result.payload;
    msg.summary = result.summary;
    return msg;
} else {
    return null;
}
